style.css.contents:
* {
    margin: 0;
    box-sizing: border-box;
}

body {
    height: 100vh;
    font-family: 'Exo';
    display: grid;
    justify-content: center;
    background-color: skyblue;
}

img {
    width: auto;
    height: auto;
    max-width: 300px;
    max-height: 200px;
}

input {
    font-size: 1rem;
    padding: 5px 10px;
    width: 250px;
    text-align: center;
}

button {
    padding: 5px 10px;
}

img, h4, input, button {
    margin-top: 10px;
}

#content {
    margin-top: 30px;
    max-width: 450px;
    text-align: center;
}

#finishedStory {
    line-height: 2rem;
    padding: 20px;
}

.highlight {
    color: tomato;
    font-family: 'Shadows Into Light';
    background-color: white;
    padding: 0 5px;
    border-radius: 5px;
}
#BEGIN_EDITABLE##END_EDITABLE#

script.js.contents:
var story = 'The (color) Dragon is the (superlative) Dragon of all. It has (adjective1) (body_parts), and a (body_part) shaped like a (noun). It loves to eat (animal), although it will feast on nearly anything. It is (adjective2) and (adjective3). You must be (adjective4) around it, or you may end up as its meal!';

var questions = {
    color: 'Type a color',
    superlative: 'Type a superlative (ending is "est") (2/10)',
    body_parts: 'Type a body part (plural) (3/10)',
    body_part: 'Type a body part (4/10)',
    noun: 'Type a noun (5/10)',
    animal: 'Type an animal (plural) (6/10)',
    adjective1: 'Type an adjective (7/10)',
    adjective2: 'Type an adjective (8/10)',
    adjective3: 'Type an adjective (9/10)',
    adjective4: 'Type an adjective (Almost Done!)',
};

var answers = {};

var keys = Object.keys(questions);

questions = Object.values(questions);

submit.onclick = () => {
    if (questions.length === 0) {
        showStory();
    }
    else if (answer.value.trim().length) {
        showNextQuestion();
        const key = keys.shift();
        answers[key] = answer.value;
        answer.value = '';
        answer.focus();
    }
};

answer.onkeydown = () => {
    if (event.code === 'Enter') submit.click();
};

function showNextQuestion() {
    question.innerText = questions.shift();
}

function showStory() {
    Object.entries(answers).forEach(item => {
        const [key, answer] = item;
        story = story.replace(`(${key})`, `<span class='highlight'>${answer}</span>`);
    });
    finishedStory.innerHTML = story;
    [question, answer, submit].forEach(el => el.parentNode.removeChild(el));
}

showNextQuestion();
#BEGIN_EDITABLE##END_EDITABLE#

