// JavaScript
const wrapDefaultAnim = (config, property) => Object.assign({
    dur: 1000,
    easing: 'easeInOutQuad'
}, config, { property });
const fade = config => wrapDefaultAnim(config, 'components.material.material.opacity');
const scale = config => wrapDefaultAnim(config, 'scale');
const move = config => wrapDefaultAnim(config, 'position');
const spin = config => wrapDefaultAnim(config, 'rotation');

let scene, grid, rig, btnNext, instructions, camera, basicShapes, cut = 0;

window.addEventListener('load', init);

const actions = [
    () => {
        const text = {
            button: 'Next: Camera',
            heading: 'Axis',
            body: [
                'The X, Y and Z axis are an important aspect of 3D space.',
                '',
                '- they are used to measure distances in the scene',
                `- the point where all 3 axis intersect is the scene's origin, i.e. 0 0 0`,
                '- X is red, Y is green and Z is blue',
