        setAttr(btnNext, { position: '3.6 1.2 2.5' });

        updateText(text);
    },
    () => {
        const text = {
            button: 'Next: Get Started',
            heading: 'Field of View',
            body: 'The horizontal field of view is much wider than the vertical.'
        };

        setAttr(rig.root, 'camera-rig', { length: 2, swivel: 25, elevation: 6, panTilt: '-55 -20' });
        setAttr(camera.vfov, { visible: false });
        setAttr(camera.hfov, { visible: true });
        setAttr(instructions, { position: '2 4 0.8', rotation: '-90 0 0', width: 2, height: 0.65 });
        setAttr(btnNext, { position: '2 4 1.4', rotation: '-90 0 0', width: 1.2 });

        updateText(text);
    },
    () => {
        const text = {
            button: '',
            heading: 'Get Started',
            body: `Now you know what 3D space looks like in A-Frame, let's begin creating our own scene!`
        };

        setAttr(rig.root, 'camera-rig', { length: 0, swivel: 0, elevation: 1.6, panTilt: '0 0' });
        setAttr(camera, { visible: false });
        // setAttr(grid.root, { visible: false });
        setAttr(basicShapes, { visible: true });
        setAttr(instructions, { position: '0 2.6 -2', rotation: '0 0 0', width: 2, height: 0.8 });

