    }
];

function updateText(text) {
    const width = instructions.components.geometry.data.width - 0.2;
    const top = instructions.components.geometry.data.height / 2 - 0.1;

    setAttr(instructions.heading, {
        width,
        position: `0 ${top} 0`,
        wrapCount: width * 10,
        value: text.heading
    });

    setAttr(instructions.body, {
        width,
        position: `0 ${top - 0.25} 0`,
        wrapCount: width * 18,
        value: text.body
    });

    setAttr(btnNext.text, { value: text.button });
}

function init() {
    scene = document.querySelector('a-scene');
    btnNext = document.querySelector('[class=clickable]');
    btnNext.text = btnNext.querySelector('a-text');
    instructions = document.querySelector('#instructions');
    instructions.heading = instructions.querySelector('#heading');
    instructions.body = instructions.querySelector('#body');
    camera = document.querySelector('[camera-model]');
    camera.vfov = camera.querySelector('#v-fov');
    camera.hfov = camera.querySelector('#h-fov');
    basicShapes = document.querySelector('#basic-shapes');

    btnNext.addEventListener('mouseenter', () => setAttr(btnNext, { color: 'purple' }));
    btnNext.addEventListener('mouseleave', () => setAttr(btnNext, { color: 'royalblue' }));
    btnNext.addEventListener('click', () => actions[cut++]());

    setAttr(instructions, { position: '0 1.8 -1.5', width: 1.6, height: 1.15 });
    setAttr(btnNext, { position: '0 1 -1.5' });
    updateText({
        button: 'Next: Axis',
        heading: '3D Space',
        body: [
            `This what you see after setting up A-Frame, it's doesn't seem much but we're actually inside a 3D space.`,
            '',
            `Let's take a closer look.`
        ].join('\n')
     });
