AFRAME.registerComponent('camera-rig', {
    schema: {
        pivot: { type: 'vec3', default: '0 0 0' },
        length: { default: 3 },
        swivel: { default: 0 },
        elevation: { default: 1 },
        panTilt: { type: 'vec2', default: { y: 0, x: 0 } }
    },
    init: function () {
        const swivel = document.createElement('a-entity');
        const elevator = document.createElement('a-entity');
        const gimbal = document.createElement('a-entity');
        const camera = document.createElement('a-camera');
        const data = this.data;

        setAttr(swivel, { rotation: `0 ${data.swivel} 0` });
        setAttr(elevator, { position: `0 ${data.elevation} 0` });
        setAttr(gimbal, { position: `0 0 ${data.length}`, rotation: `${data.panTilt.x} ${data.panTilt.y} 0` });
        setAttr(camera, { position: '0 0 0', wasdControlsEnabled: false, mouseCursor: '' });
        setAttr(this.el, { position: data.pivot });

        this.el.append(swivel);
        swivel.append(elevator);
        elevator.append(gimbal);
        gimbal.append(camera);

        rig = {
            swivel, elevator, gimbal, camera,
            root: this.el,
            map: {
                pivot: { el: 'root', fn: move, to: v => v },
                length: { el: 'gimbal', fn: move, to: v => `0 0 ${v}` },
                elevation: { el: 'elevator', fn: move, to: v => `0 ${v} 0` },
                swivel: { el: 'swivel', fn: spin, to: v => `0 ${v} 0` },
                panTilt: { el: 'gimbal', fn: spin, to: v => `${v.x} ${v.y} 0` }
            }
        };
    },
    update: function (oldData) {
        if (Object.keys(oldData).length === 0) return;

        const newData = AFRAME.utils.diff(oldData, this.data);
        console.log(newData)
        Object.entries(newData).forEach(([key, val]) => {
            console.log(rig.map[key]);
            const { el, fn, to } = rig.map[key];
            const anim = `animation__${key}`;

            setAttr(rig[el], { [anim]: fn({ to: to(val) }) });
            rig[el].addEventListener(`animationcomplete__${key}`, () => rig[el].removeAttribute(anim));
        });
    }
});

AFRAME.registerComponent('grid', {
    init: function () {
        const lines = {
            x: document.createElement('a-draw-curve'),
            y: document.createElement('a-draw-curve'),
            z: document.createElement('a-draw-curve'),
        };
        const markers = {
            x: document.createElement('a-entity'),
            y: document.createElement('a-entity'),
            z: document.createElement('a-entity')
        }
        const colors = {
            x: 'red',
            y: 'green',
            z: 'blue'
        };

        ['x', 'y', 'z'].forEach(axis => {
            const curve = document.createElement('a-curve');
            const start = document.createElement('a-curve-point');
            const end = document.createElement('a-curve-point');
            const pos = { x: 0, y: 0, z: 0 };
            const far = 25;

            pos[axis] = far;
            setAttr(start, { position: Object.values(pos).join(' ') });

            pos[axis] = -far;
            setAttr(end, { position: Object.values(pos).join(' ') });

            curve.id = `curve-${axis}`;
            curve.append(start, end);
            this.el.append(curve);

            setAttr(lines[axis], {
                curveref: `#curve-${axis}`,
                material: {
                    shader: 'line',
                    color: colors[axis]
                }
            });

            setAttr(markers[axis], {
                cloneAlongCurve: {
                    curve: curve,
                    scale: '0.2 0.02 0.02'
                },
                geometry: { primitive: 'box' },
                material: {
                    shader: 'flat',
                    color: colors[axis]
                }
            });
        });

        grid = { root: this.el, lines, markers };

        this.el.append(lines.x, lines.y, lines.z, markers.x, markers.y, markers.z);
        setAttr(this.el, { visible: false });
    }
});

function setAttr(...props) {
    const el = props.shift();
    const config = props.pop();

    Object.entries(config).forEach(([key, value]) => {
        // console.log(key.replace(/[A-Z]/g, '-$&').toLowerCase());
        key = props.length ? key : key.replace(/[A-Z]/g, '-$&').toLowerCase();
        el.setAttribute(...props.concat([key, value]));
    });
}

