function concatText(...string) {
    return string.join('\n');
}

function updateText(text, mod = 1) {
    const width = instructions.components.geometry.data.width - 0.2;
    const height = instructions.components.geometry.data.height;
    const { x, y, z } = instructions.components.position.data;
    const margin = Math.max(0.12, height / 10);
    const top = height / 2 - margin;

    setAttr(instructions.heading, {
        width,
        position: `0 ${top} 0`,
        wrapCount: 12 * mod,
        value: text.heading
    });

    setAttr(instructions.body, {
        width,
        position: `0 ${-top} 0`,
        wrapCount: 20 * mod,
        value: text.body
    });

    setAttr(btnNext, {
        position: `${x} ${y - height / 2 - btnNext.components.geometry.data.height / 2 - margin} ${z}`,
        rotation: instructions.components.rotation ? instructions.components.rotation.data : '0 0 0'
    });

    setAttr(btnNext.text, { width: btnNext.components.geometry.data.width - 0.2, value: text.button });
}

function setAttr(...props) {
    const el = props.shift();
    const config = props.pop();

    Object.entries(config).forEach(([key, value]) => {
        key = props.length ? key : key.replace(/[A-Z]/g, '-$&').toLowerCase();
        el.setAttribute(...props.concat([key, value]));
    });
}


