// JavaScript
var button;
var color1 = 'royalblue';
var color2 = 'lightgreen';
var buttonPressAnimation = {
    property: 'position',
    to: '0 0.5 0',
    dur: 200,
    easing: 'easeOutQuad'
};
var buttonReleaseAnimation = {
    property: 'position',
    to: '0 0.6 0',
    dur: 200,
    easing: 'easeInQuad'
};

AFRAME.registerComponent('cursor-listener', {
    init: function () {
        //  this.el is the element that has the "cursor-listener" attribute, i.e. the red button
        button = this.el
        button.addEventListener('click', startPressAnimation);
    }
});

function startPressAnimation() {
    button.setAttribute('animation__press', buttonPressAnimation);
    button.addEventListener('animationcomplete__press', changeColor);
}

function changeColor() {
    var box = document.querySelector('a-box');
    var currentColor = box.getAttribute('color');

    if (currentColor == color1) {
        box.setAttribute('color', color2);
    }
    else {
        box.setAttribute('color', color1);
    }

    button.removeAttribute('animation__press');
    button.setAttribute('animation__release', buttonReleaseAnimation);
    button.addEventListener('animationcomplete__release', removeReleaseAnimation);
}

function removeReleaseAnimation() {
    button.removeAttribute('animation__release');
}

//  JavaScript
function teleport() {
    //  take the first <a-camera> element from the scene as the viewer
    var viewer = document.querySelector('a-camera');
    //  get the position of the object that triggered this the "teleport" function
    var target = event.target.getAttribute('position');

    //  move the viewer to the target position
    viewer.setAttribute('position', target);
    //  remove the target object from the scene
    event.target.remove();

    //  find the next waypoint (only looks for <a-dodecahedron> elements)
    var waypoint = document.querySelector('a-dodecahedron');

    //  if waypoint is found
    if (waypoint) {
        //  set up click event listener
        waypoint.addEventListener('click', teleport);
        //  change the color of the waypoint
        waypoint.setAttribute('color', 'tomato');
    }
    //  no more <a-dodecahedron> object left in the scene
    else {
        //  go to another VR world
        window.location.href = 'https://app-staging.bsd.education/share/QR5QmY7b';
    }
}

// JavaScript

window.onload = addBoxes;

function addBoxes() {
    const scene = document.querySelector('a-scene');
    let boxes = 0;

    while (boxes < 100) {
        const box = document.createElement('a-box');
        const attr = randomAttr();

        box.setAttribute('scale', attr.size);
        box.setAttribute('position', attr.pos);
        box.setAttribute('rotation', `0 ${randomInRange(0, 90)} 0`);
        box.setAttribute('color', 'dimgrey');
        box.setAttribute('roughness', '1');

        scene.append(box);
        boxes++;
    }
}

function randomAttr() {
    const dx = randomInRange(0, 25) * randomSign();
    const dz = randomInRange(0, 25) * randomSign();
    const dist = Math.sqrt(dx ** 2 + dz ** 2);

    if (dist < 6 || dist > 25) {
        return randomAttr();
    }

    const sx = 1 + Math.random();
    const sy = 1 + Math.random() * 3;
    const sz = 1 + Math.random();
    const dy = sy / 2;

    return {
