let ball, silent = true;

window.addEventListener('keydown', () => silent = !silent);

AFRAME.registerComponent('bounce', {
    init: function() {
        ball = this.el;
        fall();
    }
});

function fall() {
    ball.removeAttribute('animation__up');
    ball.setAttribute('animation__down', {
        property: 'position',
        from: '0 1.8 -2.5',
        to: '0 0 -2.5',
        dur: 500,
        easing: 'easeInCubic'
    });
    ball.addEventListener('animationcomplete__down', squash);
}

function squash() {
    if (!silent) {
        const sound = document.querySelector('#bounce-sound');    
        setTimeout(() => sound.emit('flat'), 20);
    }

    ball.removeAttribute('animation__down');
    ball.setAttribute('animation__squash', {
        property: 'scale',
        from: '1 1 1',
        to: '1.3 0.5 1',
        easing: 'easeOutCubic',
        dur: 40,
        dir: 'alternate',
        loop: 1
    });
    ball.addEventListener('animationcomplete__squash', rebound);
}

function rebound() {
    ball.removeAttribute('animation__squash');
    ball.setAttribute('animation__up', {
        property: 'position',
        from: '0 0 -2.5',
        to: '0 1.8 -2.5',
        dur: 500,
        easing: 'easeOutCubic'
    });
    ball.addEventListener('animationcomplete__up', fall);
}
