// JavaScript
var scene;
var button;
var box;
var buttonPressAnimation = {
    property: 'position',
    to: '0 0.5 0',
    dur: 200,
    easing: 'easeOutQuad'
};
var buttonReleaseAnimation = {
    property: 'position',
    to: '0 0.6 0',
    dur: 200,
    easing: 'easeInQuad'
};

window.onload = init;

function init() {
    scene = document.querySelector('#scene');
    button = document.querySelector('#button');
    box = document.querySelector('#box');

    button.addEventListener('click', pressButton);
}

function pressButton() {
    button.setAttribute('animation__press', buttonPressAnimation);
    button.addEventListener('animationcomplete__press', changeColor);
}

function changeColor() {
    if (box.getAttribute('color') === 'gold') {
        box.setAttribute('color',  'dodgerblue');
    }
    else {
        box.setAttribute('color', 'gold');
    }

    button.removeAttribute('animation__press');
    button.setAttribute('animation__release', buttonReleaseAnimation);
    button.addEventListener('animationcomplete__release', releaseButton);
}

function releaseButton() {
    button.removeAttribute('animation__release');
}

var scene;
var sphere;
var scoreText;
var points = 0;

window.addEventListener('load', init);

function init() {
    scene = document.querySelector('a-scene');
    sphere = document.querySelector('#sphere');
    scoreText = document.querySelector('#scoreText');
}

AFRAME.registerComponent('destroy-on-hit', {
    init: function () {
        //  this.el represents the object that has the destroy-on-hit component
        //  the 'collisions' event comes from the aframe-physics library
        this.el.addEventListener('collisions', destroy);
    }
});

function destroy() {
    //  event.detail.els contains an array of all the colliders
    //  in our case it would only be the red ball
    //  but it's a good idea to always check what objects are colliding with
    //  to determine what should happen next 
    if (event.detail.els[0] === sphere) {
        scene.removeChild(event.target);
        points++;
        scoreText.setAttribute('value', 'Boxes: ' + points);

        if (points === 4) {
            scoreText.setAttribute('value', 'Done');
        }
    }
}
