style.css.contents:
@import url("https://fonts.googleapis.com/css?family=Quicksand|Roboto&display=swap");

* {
    box-sizing: border-box;
}

body {
    margin: 0;
    padding: 0;
    font-family: "Roboto", sans-serif;
}

h1,
h2,
h3,
h4,
h5,
h6 {
    font-family: "Quicksand", sans-serif;
}

h2 {
    font-size: 30px;
}

p {
    font-size: 16px;
}

/* 
	WEBSITE NAVIGATON BAR 
*/

#header {
    height: 120px;
    background-color: #303f9f;
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    justify-content: space-around;
}

#logo {
    width: 80px;
}

ul {
    margin: 0;
    padding: 0;
    text-align: center;
    z-index: 10;
}

li {
    color: #ffffff;
    display: inline-block;
    margin: 0 0 0 20px;
    cursor: pointer;
    font-size: 22px;
    font-family: "Quicksand", sans-serif;
    font-weight: 700;
    transition: 0.3s;
}

li:hover,
li.active {
    color: #ffd600;
    transform: scale(1.1);
}

/* 
	HOME PAGE
*/

#home-banner {
    height: 300px;
    background-image: url("https://app.bsd.education/resources/background-2731956_1920.png");
    background-size: cover;
    background-position: center center;
    position: relative;
    text-align: center;
    color: #fff;
    margin-left: -20px;
    margin-right: -20px;
    margin-top: -20px;
    display: flex;
    flex-direction: column;
    justify-content: center;
}

#home-banner h1 {
    margin: 0;
    font-size: 32px;
}

#home-banner h2 {
    font-size: 18px;
    margin: 10px 0 0;
}

#home img {
    width: 30%;
    margin: 20px;
}

/* 
	ABOUT PAGE
*/

#about {
    padding: 20px 30px;
}

#about h2 {
    color: #303f9f;
    text-align: center;
}

/* 
	SERVICE PAGE
*/

#service h2 {
    color: #303f9f;
    text-align: center;
}

#service h3 {
    text-align: center;
}

.row {
    margin-top: 30px;
    display: flex;
    text-align: left;
}

.row img {
    width: 100px;
    margin-right: 20px;
}

/* 
	FOOTER
*/

#footer {
    background-color: #303f9f;
    position: absolute;
    bottom: 0;
    height: 50px;
    width: 100%;
}

#footer p {
    font-size: 14px;
    color: white;
    text-align: center;
}

#page-container {
    position: relative;
    min-height: 100vh;
}

#content {
    padding: 100px 0 50px;
}

#content > div {
    padding: 20px 20px;
    display: none;
}

#header {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    z-index: 100;
}

/* For mobile view - do NOT edit the CSS below! */
@media screen and (max-width: 540px) {
    .row,
    .inner-row {
        flex-wrap: wrap;
        text-align: center;
    }

    #home-banner {
        height: 40vh;
    }

    #home img {
        width: 200px;
        display: block;
        margin: 20px auto;
    }

    .row img {
        display: block;
        margin: 20px auto;
    }

    #header {
        height: 12vh;
        min-height: 100px;
    }

    ul {
        position: relative;
    }

    ul li {
        font-size: 18px;
    }
}

script.js.contents:
document.querySelectorAll("#header ul li").forEach(function (page) {
    page.onclick = () => {
        let pageName = page.id.split("-")[1];
        showPage(pageName);
    }
});

function showPage(pageName) {
    clearPages();
    var current_page = document.getElementById(pageName);
    current_page.style.display = "block";
}

function clearPages() {
    var pages = document.querySelectorAll("#content > div");

    for (var i = 0; i < pages.length; i++) {
        pages[i].style.display = "none";
    }
}

showPage('home');

