@import url("https://fonts.googleapis.com/css?family=VT323");
* {
    margin: 0;
    box-sizing: border-box;
    user-select: none;
}

body, html {
    height: 100vh;
    font-family: "VT323";
    text-align: center;
    background-color: #333;
}

h1 {
    color: ghostwhite;
    font-size: 60px;
    font-weight: normal;
}

p {
    margin-top: 10px;
    color: gray;
    font-size: 18px;
}

span {
    font-size: 36px;
}

button {
    padding: 5px 20px;
    border-radius: 10px;
    color: silver;
    font-family: "VT323";
    font-size: 36px;
    background-color: #111;
    cursor: pointer;
    border: none;
    outline: none;
}

#gameArea {
    position: relative;
    margin: 10px auto;
    padding: 0 10px;
    background-color: black;
    overflow: hidden;
    cursor: none;
}

#playerPad, #aiPad, #ball {
    position: absolute;
}

#playerScore {
    margin-right: 10px;
    color: dodgerblue;
}

#aiScore {
    color: tomato;
}

#playerPad, #aiPad {
    border-radius: 5px;
}

#playerPad {
    background-color: dodgerblue;
}

#aiPad {
    background-color: tomato;
    transition: top 0.2s linear;
}

#ball {
    background-color: silver;
    border-radius: 50%;
}
#BEGIN_EDITABLE##END_EDITABLE#

